INSERT INTO `phenotype` VALUES (1,'Absent anterior commissure','AC',NULL,'Congenital absence of the anterior commissure, one of the white matter commissures of the forebrain.',NULL,NULL,'2013-03-13 00:21:33'),(2,'Agenesis of the corpus callosum','ACC',NULL,'A spectrum of abnormalities of the corpus callosum, the principal supratentorial white matter commissure.','A) T1 sagittal and B) T2 axial view of complete agenesis of the corpus callosum','Hetts SW, et al, AJR 2006','2013-03-07 21:56:50'),(3,'Agyria','AGYR','','Absent convolutions of the brain','','Friede RL, 1989','2013-02-18 17:08:00'),(7,'Autism','AUT','','A disorder beginning in childhood marked by the presence of abnormal or impaired development in social interaction and communication and a markedly restricted repertoire of activity and interest. Manifestations of the disorder vary greatly depending on the developmental level and chronological age of the individual (DSM-IV).','','HP:0000717','2013-02-18 17:08:00'),(8,'Band-like calcifications','BCALC',NULL,'Intracerebral calcification in a band-like distrubution in the subcortical white matter.',NULL,'Briggs TA, et al, Am J Med Genet 2008 PMID: 19012351','2013-03-07 22:02:37'),(9,'Bilateral frontoparietal polymicrogyria','BFPP',NULL,'Polymicrogyria affecting the frontal and parietal lobes bilaterally',NULL,'Leventer RJ, et al, Brain 2010','2013-03-09 20:53:26'),(10,'Cerebral calcifications','CALC','','Intracerebral calcifications.','','Barkovich','2013-02-18 17:08:00'),(11,'Cerebellar atrophy','CBLA',NULL,'A small cerebellum with enlarged fissures compared to the folia, or if the cerebellum has undergone progressive loss of volume.','Cerebellar atrophy seen on (A) T1 sagittal, (B) T2 axial, and (C) T2 coronal views','Barkovich, 4th ed. p. 167.','2013-03-07 22:06:32'),(12,'Cerebellar hypoplasia','CBLH','','A small cerebellum with fissures of normal size compared to the folia.','','Barkovich, 4th ed. p. 167.','2013-02-18 17:08:00'),(13,'Cerebellar tonsillar ectopia','CBTE','Chiari malformation','Abnormal position of the cerebellar tonsils, crowding or protruding below the foramen magnum.','Progression of cerebellar tonsillar ectopia towards the foramen magnum at (A) 1 day, (B) 4 months, and (C) 11 months',NULL,'2013-03-07 22:09:52'),(14,'Cerebellar vermal hypoplasia with mega cisterna magna','CBLVH-MCM','','Hypoplastic inferior cerebellar vermis with a large cisterna magna','','Barkovich, 4th ed. p. 387','2013-02-18 17:08:00'),(15,'Cerebellar vermis hypoplasia','CBLVH','','','','','2013-02-18 17:08:00'),(16,'Cobblestone malformation','COB','','Severe malformation of the basement membrane and neuronal migration consisting of cobblestone cortex, abnormal white matter, enlarged ventricles (often with hydrocephalus), small brain stem and small dysplastic cerebellum. In the most severely affected patients the brain surface is smooth, which led to the designation as lissencephaly, although less severe cobblestone malformations have an irregular, pebbled surface rather than a smooth surface','','','2013-02-18 17:08:00'),(17,'Congenital microcephaly','CMIC','Primary microcephaly','Microcephaly (head circumference less than 3rd centile) present at birth',NULL,NULL,'2013-03-09 19:54:53'),(18,'Cerebral palsy','CP','Familial cerebral palsy','Cerebral palsy describes a group of permanent disorders of the development of movement and posture, causing activity limitation, that are attributed to nonprogressive disturbances that occurred in the developing fetal or infant brain.',NULL,'Human Phenotype Ontology HP:0100021','2013-03-13 01:44:59'),(19,'Dandy-Walker malformation','DWM','','Enlarged posterior fossa with high position of the tentorium, hypogenesis or agenesis of the cerebellar vermis, and a cystic dilatation of the fourth ventricle.','','Barkovich, 4th ed, p. 387','2013-02-18 17:08:00'),(20,'Developmental encephalopathy','DEVN','Autism-plus','A group of disorders that feature impairments in multiple domains of neurologic development and function.','Some of the domains commonly affected in developmental encephalopathies.',NULL,'2013-03-13 01:42:51'),(21,'Developmental epilepsy','DEVE',NULL,'A group of age-dependent epilepsy syndromes with usually severe cognitive outcome that includes early myoclonic encephalopathy (EME), early infantile epileptic encephalopathy (EIEE), early onset epileptic encephalopathy (EOEE), infantile spasms (ISS), and Lennox-Gastaut syndrome (LGS).','The age dependent epilepsies that have phenotype and genotype overlap.','Paciorkowski, Thio, Dobyns, Pediatr Neurol 2011','2013-03-13 01:44:02'),(22,'Dyslexia','DYSLEX',NULL,'A learning disorder characterized primarily by difficulties in learning to read and spell. Dyslectic children also exhibit a tendency to read words from right to left and to confuse letters such as b and d whose orientation is important for their identification. Children with dyslexia appear to be impaired in phonemic skills (the ability to associate visual symbols with the sounds they represent).',NULL,'Human Phenotype Ontology HP:0010522','2013-03-13 01:45:26'),(23,'Dystonia','DYST',NULL,'An attitude or posture due to the co-contraction of agonists and antagonist muscles in one region of the body. It most often affects the large axial muscles of the trunk and limb girdles.',NULL,'MeSH 68004421','2013-03-13 01:46:14'),(24,'Early infantile epileptic encephalopathy','EIEE','','Tonic seizures with burst-suppresion EEG pattern','','Panayiotopoulos','2013-02-18 17:08:00'),(25,'Early myoclonic encephalopathy','EME','','','','','2013-02-18 17:08:00'),(26,'Early onset epileptic encephalopathy','EOEE','','','','','2013-02-18 17:08:00'),(27,'Encephalocele','ENCEPH','Cephalocele','Brain tissue herniation through a congenital or acquired defect in the skull. The majority of congenital encephaloceles occur in the occipital or frontal regions.',NULL,'MeSH 68004677','2013-03-13 01:47:23'),(28,'Epilepsy','EPI','','\"Two or more unprovoked seizures. Seizures are characterized by recurrent episodes of paroxysmal brain dysfunction due to a sudden, disorderly, and excessive neuronal discharge.','','MeSH 68004827 (modified)','2013-02-18 17:08:00'),(29,'Porencephaly','POR','Familial porencephaly','Any full-thickness defect within the cerebral mantle, irrespective of etiology, or time of onset.','','','2013-02-18 17:08:00'),(30,'Heterotopia','HET',NULL,'Groups of cells found in an inappropriate location in the correct tissue of origin. Heterotopia may be periventricular, subcortical, or columnar in location.',NULL,NULL,'2013-03-13 02:03:53'),(31,'Holoprosencephaly','HPE',NULL,'Anterior midline brain, cranial, and facial malformations resulting from the failure of the embryonic prosencephalon to undergo segmentation and cleavage.','Several forms of holoprosencephaly are recognized, including alobar (A), semilobar (B), lobar (C), and the middle hemispheric variant (D).','MeSH 68016142','2013-03-13 02:10:00'),(32,'Hydrocephalus','HYD',NULL,'Disturbance of formation, flow, or absorption of CSF that leads to an increase in volume occupied by this fluid in the central nervous system.',NULL,'Rekate HL, Semin Pediatr Neurol., 2009','2013-03-13 02:11:38'),(33,'Hyperekplexia','HEKPLEX',NULL,'Exaggerated startle response.',NULL,'Human Phenotype Ontology HP:0002267','2013-03-13 02:13:35'),(34,'Infantile spasms','ISS','','Epileptic spasms with ictal electrodecrement, usually associated with hypsarrhythmia.','','Panayiotopoulos','2013-02-18 17:08:00'),(35,'Lissencephaly','LIS',NULL,'Smooth brain with paucity of gyri and sulcal development on the surface of the brain.','Lissencephaly seen in T2 axial view (A) and T2 coronol view (B). Note the characteristic \"figure 8\" finding on axial view.','Barkovich, 4th ed., p 346.','2013-03-07 23:11:10'),(36,'Lennox-Gastaut syndrome','LGS','','','','','2013-02-18 17:08:00'),(37,'Megalencephaly','MEG','','Oversized and overweight brain that exceeds the mean by two standard deviations or more due to either increased in the number of size of cells','','DeMyer, 1970','2013-02-18 17:08:00'),(38,'Intellectual disability','ID','Mental retardation','Subnormal intellectual functioning which originates during the developmental period. Intellectual disability, previously referred to as mental retardation, has been defined as an IQ score below 70.',NULL,'Human Phenotype Ontology HP:0001249','2013-03-13 02:17:11'),(39,'Microcephaly','MIC',NULL,'Occipito-frontal (head) circumference (OFC) less than 3rd centile compared to appropriate, age matched, normal standards.','Head circumference growth chart showing ranges of microcephaly, from mild (-2 to -3 SD from the mean) to severe (smaller than -6 SD).','HP:0000252','2013-03-09 20:41:27'),(40,'Microcephaly with simplified gyral pattern','MSG',NULL,'Congenital microcephaly associated with a reduced number of gyri, thin or normal cortical thickness, and reduced number and depth of sulci',NULL,'Dobyns, Am J Med Genet., 2002','2013-03-13 02:38:26'),(42,'Microphthalmia','MOPTH','Microphthalmos, includes anophthalmia','Congenital or developmental anomaly in which the eyeballs are abnormally small.',NULL,'MeSH 68008850','2013-03-13 02:40:30'),(43,'Molar tooth malformation','MTM','Molar tooth sign','A malformation of the brainstem and cerebellum that resembles a tooth when seen on axial MRI. Characterized by 1) Hypoplastic, high-lying cerebellar vermis (best seen on sagittal midline view) 2) Deficiency of the dorsal midbrain with a narrow isthmus and abnormally deep interpeduncular fossa (seen on axial view) 3) Large, elongated cerebellar peduncles (seen on axial view).','Characteristic features of molar tooth malformation seen on sagittal view (A) showing cerebellar vermis hypoplasia, axial (B and C) showing narrow isthmus/deep interpeduncular fossa and elongated cerebellar peduncles.','Barkovich, 4th ed. p. 392-393','2013-03-07 23:32:34'),(44,'Optic nerve hypoplasia','ONH','','','','','2013-02-18 17:08:00'),(45,'Pachygyria','PGY','','Presence of a few broad, flat gyri with thickened cortex, used interchangeably with the term \"incomplete lissencephaly\".','','Barkovich, 4th ed. p. 346.','2013-02-18 17:08:00'),(46,'Periventricular nodular heterotopia','PNH','','Periventricular collections of nerve cells secondary to arrest of radial migration of neurons.','','Barkovich, 4th ed. p. 352','2013-02-18 17:08:00'),(47,'Polymicrogyria','PMG',NULL,'Regions of apparent cortical thickening with an irregular cortical surface, and a \"stippled\" grey-white junction. A postmigration malformation of cortical development characterized by fusion of the molecular layer but preserved laminar neuronal organization.','Several patterns of polymicrogryia are illustrated. The most common, bilateral perisylvian is seen on sagittal (A), with the characteristic elongated sylvian fissure, and axial views (B and C). Axial views of bifrontal-parietal (D), parietal-occipital (E), and generalized polymicrogyria (F).','Leventer RJ, et al, Brain 2010','2013-03-11 01:23:44'),(48,'Pontocerebellar hypoplasia','PCH',NULL,'Combined hypoplasia and atrophy of the pons and the cerebellum present at birth.',NULL,'Barth, et al, Acta Neuropathol. 2007','2013-03-13 02:59:40'),(49,'Pituitary malformation','PIT',NULL,'Congenital abnormalities of the formation of the pituitary',NULL,NULL,'2013-03-13 02:49:44'),(50,'Postnatal microcephaly','PMIC','','Microcephaly (head circumference 3 standard deviations below the mean) with onset after birth','','','2013-02-18 17:08:00'),(51,'Rhombencephalosynapsis','RSYN',NULL,'Midline fusion of the two cerebellar hemispheres  that may be partial or complete, with absence of an identifiable vermis.',NULL,NULL,'2013-03-13 03:03:26'),(52,'Schizencephaly','SCZ','','Clefts lined with gray matter that extend through the entire hemisphere, from the ependymal lining of the lateral ventricles to the pial covering of the cortex.','','Barkovich, 4th ed. p 334','2013-02-18 17:08:00'),(54,'Sensorineural deafness','SND',NULL,'A complete absence of the ability for sensory perception of sound in one or both ears related to an abnormal functionality of the cochlear nerve.',NULL,'Human Phenotype Ontology HP:0008538','2013-03-13 03:04:47'),(55,'Subcortical band heterotopia','SBH',NULL,'A malformation of cortical development caused by the failure of a subpopulation of migrating neurons to reach the developing cortical plate. The heterotopic neurons form a bilateral, symmetric band of gray matter located below the cortical ribbon and separated from it by a thin band of white matter.',NULL,'Sicca F, et al, Neurology 2003','2013-03-13 03:10:51'),(56,'Ventriculomegaly','VMEG','','','','','2013-02-18 17:08:00'),(57,'Central hypoventilation','CHYPOV',NULL,'A condition associated with multiple episodes of apnea which are distinguished from obstructive apnea by the complete cessation of efforts to breathe. This disorder is associated with dysfunction of central nervous system centers that regulate respiration.',NULL,'MeSH 68020182 (modified)','2013-03-13 01:35:49'),(58,'Macrocephaly','MAC',NULL,'Occipitofrontal circumference greater than 2 standard deviations above the mean.',NULL,'MeSH 68058627','2013-03-13 02:36:14'),(59,'Retinal dystrophy','RDYST',NULL,'A group of disorders involving predominantly the posterior portion of the ocular fundus, due to degeneration in the sensory layer of the retina, retinal pigment epithelium, bruch membrane, choroid, or a combination of these tissues.',NULL,'MeSH 68058499','2013-03-13 03:01:35'),(61,'Craniosynostosis','Cransyn',NULL,'Premature fusion of one or more cranial sutures as a developmental error.',NULL,'Barkovich, 4th ed. p. 410','2013-03-03 19:37:04'),(62,'Cortical tubers','TUBER',NULL,NULL,NULL,NULL,'2013-03-04 02:10:24'),(63,'Chorea','CHOREA',NULL,'Involuntary, forcible, rapid, jerky movements that may be subtle or become confluent, markedly altering normal patterns of movement.',NULL,'MeSH 68002819','2013-03-13 01:39:23'),(64,'Parkinsonism','PARK',NULL,'Impaired motor control characterized by bradykinesia, muscle rigidity, tremor, and postural instability.',NULL,'MeSH 68020734','2013-03-13 02:45:30'),(65,'Malignant migrating partial seizures of infancy','MMPSI','Malignant partial epilepsy of infancy',NULL,NULL,NULL,'2013-03-05 02:03:19'),(66,'Frontal lobe epilepsy','FLE',NULL,'A localization-related (focal) form of epilepsy characterized by seizures which arise in the frontal lobe.',NULL,'MeSH 68017034','2013-03-13 01:56:53'),(67,'Partial epilepsy','PEPI','Focal epilepsy',NULL,NULL,NULL,'2013-03-05 03:00:31'),(68,'Bilateral perisylvian polymicrogyria','BPSPMG',NULL,'Polymicrogyria centered around the perisylvian region(s). This is the most common pattern of polymicrogyria (60%), and 85% are bilateral.','Sagittal T1 view of perisylvian polymicrogyria, showing characteristic elongated sylvian fissure. T1 axial views (B and C) show cortical irregularities in bilateral perisylvian regions.','Leventer RJ, et al, Brain 2010','2013-03-09 21:01:31'),(69,'Generalized polymicrogyria','GPMG',NULL,'Polymicrogyria that includes the entire cortical surface, from the frontal to the occipital pole. A less common pattern of polymicrogyria, accounting for about 13% of cases.',NULL,'Leventer RJ, et al, Brain 2010','2013-03-09 20:53:12'),(70,'Hemimegalencephaly','HMEG',NULL,'Overgrowth of all or part of a cerebral hemisphere.','Right-sided hemimegalencephaly on axial (A) and coronal (B) view.','Barkovich, 4th ed. p. 337','2013-03-07 23:33:52'),(71,'Focal cortical dysplasia','FCD',NULL,NULL,'An area of focal cortical dysplasia can be seen in the left occipital lobe (A) and exhibits T2 hyperintensity on FLAIR (B).',NULL,'2013-03-07 23:12:30'),(72,'Alternating hemiplegia','ALTHEMI',NULL,'Severe or complete loss of motor function on one side of the body, in this case manifesting in an alternating pattern.',NULL,'MeSH 68006429','2013-03-13 01:33:00');
